import React from "react";
import "./Byline.scss";

interface BylineProps {
    logo: string;
    header: string;
    image: string;
    creditName?: string;
    link: string;
    children: React.ReactNode;
}

const Byline = (BylineProps: BylineProps) => {
    const { logo, header, image, creditName, children, link } = BylineProps;
    return (
        <div className="Byline">
            <a href={link}>
                <img
                    className="Logo"
                    src={require(`../../assets/${logo}`)}
                    alt=""
                />
                <h2>{header}</h2>
                <img
                    className="Image"
                    src={require(`../../assets/${image}`)}
                    alt=""
                />
                <p className="Credit">{creditName}</p>
                <p>{children}</p>
            </a>
        </div>
    );
};

export default Byline;
